mapper.select:
SELECT pm.product_id, pm.category, pm.name, pm.color_code, pm.color, pm.price, pimg.img_name, pimg.id FROM product_mst pm LEFT OUTER JOIN product_img pimg ON(pimg.product_id = pm.product_id) WHERE 1 = 1
and pm.category = #{category}
GROUP BY pm.name, pm.color_code

mapper.insert:
insert
    into cart_items
        values (
                    (
                    select
                        cm.cart_id
                    from
                        cart_mst cm
                    where
                        cm.member_id = #{username}
                    ),
                    (
                    select
                        pm.product_id
                    from
                        product_mst pm
                    where
                        pm.name = #{name}
                    and
                        pm.color_code = #{colorCode}
                    ),
                    1
                );
